            } else {
                return region;
            }
        })
        if(check === 1) {
            newRegion[0].status = 0;
        }
        setregionDataByLecture(newRegion);
    }

    const clickTimezone = (time) => {
        var status=0;
        if(time==="morning") {
            if(editLectureInfo.morning === 0)
                status=1;
            seteditLectureInfo({
                ...editLectureInfo,
                morning: status
            })
        }
        else if(time==="afternoon") {
            if(editLectureInfo.afternoon === 0)
                status=1;
            seteditLectureInfo({
                ...editLectureInfo,
                afternoon: status
            })
        }
        if(time==="evening") {
            if(editLectureInfo.evening === 0)
                status=1;
            seteditLectureInfo({
                ...editLectureInfo,
                evening: status
            })
        }
    }

    const editDayClick = (index) => {
        let updateArr = editLectureInfo.day_week_array;
        updateArr.forEach((item, i) => {
            if(i === index) {
                if(item === 0)
                    updateArr[i] = 1;
                else
                    updateArr[i] = 0;
            }
        });
        seteditLectureInfo({
            ...editLectureInfo,
            day_week_array: updateArr
        });
    }

    const clickEveryRegion = () => {
        var isEvery = regionDataByLecture[0].status;

        let newArr = regionDataByLecture.map((item, i) => { 
            if (isEvery) {  // 전국 체크 해제
                return { ...item, status: 0 }; 
            } else { 
                return { ...item, status: 1 }; 
            } 
        }); 

        setregionDataByLecture(newArr);

    }


    return (
        <div>
            <p className="admin-title-header">강의 관리</p>
            <div className="admin-content-wrapper">
                <div className="table-wrapper mt0 relative">

                    <p className="table-name mb40">
                        {isShowDelete ? "삭제된 강의 목록" : "강의 목록"}
                        <GreyButton class="right" name="강의 추가하기" click={openModal}/>
                        <GreyButton class="right mr10" name="엑셀 파일 다운로드" click={exportToCSV}/>
                    </p>

                    <div className="mb35">
                        <input className="p48 search-lecture-input" type="text" placeholder="강사명, 강의명, 강의 소개, 지역, 주제로 키워드 검색" value={keyword || ""} onChange={searchKeywordChanged}/>
                        <GreyButton name="검색" class="mr15" click={readAllLecture}/>
                        {!isShowDelete ?
                        <GreyButton name="삭제된 강의 보기" click={() => {setisShowDelete(true)}}/>:
                        <GreyButton name="전체 강의 보기" click={() => {setisShowDelete(false)}}/>}
                    </div>

                    <CommonModal open={isModalOpen} close={closeModal} func={createLecture} header="강의 추가하기" footer="추가하기">
                        <div className="lecture-info-wrapper">
                            <p className="admin-lecture-date">
                                <span>
                                    {allInstructors !== null && allInstructors.length !== 0 ?
                                    <select className="mr15 p4" defaultValue={addLectureInstId} onChange = {(e)=>setaddLectureInstId(e.target.value)}>
                                        {allInstructors.map((data) => 
                                            (<option key={data.id} value={data.user_id}>{data.inst_name} {data.position_name}</option>)
                                        )}
                                        {/* <option>hello</option> */}
                                    </select>:
                                    <span>강사가 없습니다.</span>
                                    }
                                    {/* {allInstructors !== null ?
                                    <select>
                                    allInstructors.map((data, index) => (
                                        <option value={data.id}>{data.name} 강사</option>
                                    ))
                                    </select>
                                    :
                                    <span>강사가 없습니다.</span>
                                    } */}
                                    작성일 {today.toLocaleString()}
                                </span>
                                {/* <span className="lecture-update">수정일 2021_07_14_일</span> */}
                            </p>
                            <div className="p4030">
                                <div className="lecture-grid-layout2 mb15">
                                    <span className="lecture-info-title">강의 제목</span>
                                    <input className="lecture-input" onChange={(e) => { setAddLectureName(e.target.value); }}/>
                                </div>
                                <hr className="lecture-hr"/>
                                <div className="category-grid mb20">
                                    <span className="lecture-info-title">가능 지역</span>
                                    <div className="category-wrapper">
                                        {regionData !== null ?
                                        regionData.map((dataUnit, index2) => (
                                            <div key={index2} className={selectedRegionData.filter(id => id === dataUnit.id || id === 0).length !== 0? "category-btn category-active": "category-btn "} onClick = {() => {selectRegion(dataUnit.id)}}>{dataUnit.name}</div>
                                        )):null}
                                    </div>
                                </div>
                                <div className="category-grid">
                                    <span className ="lecture-info-title">주제 카테고리</span>
                                    <div className="category-wrapper">
                                        {categoryData !== null ?
                                        categoryData.map((dataUnit, index2) => (
                                            <div key={index2} className={selectedCategoryData.filter(id => id === dataUnit.id).length !== 0? "category-btn category-active":"category-btn "} onClick = {() => {selectCategory(dataUnit.id)}}>{dataUnit.name}</div>
                                        )):null}
                                    </div>
                                </div>
                                <hr className="lecture-hr"/>
                                {notUsingCalendar ?
                                <div className="mb40 lecture-grid-layout">
                                    <span className="lecture-info-title">가능한 날짜</span>
                                    <span>
                                        <div className="mt5">
                                            <label className="mr10">일정 미지정</label>
                                            <input type="checkbox" onChange={(e) => setnotUsingCalendar(!notUsingCalendar)} checked/>
                                        </div>
                                    </span>
                                </div>
                                :
                                <div className="mb40 lecture-grid-layout">
                                    <span className="lecture-info-title">가능한 날짜</span>
                                    <span>
                                        <input className="lecture-input" type="date" onChange={(e) => { setAddLectureStartDate(e.target.value); }}/> ~ <input className="lecture-input" type="date"  onChange={(e) => { setAddLectureEndDate(e.target.value); }}/>
                                        <div className="mt5">
                                            <label className="mr10">일정 미지정</label>
                                            <input type="checkbox" onChange={(e) => setnotUsingCalendar(!notUsingCalendar)}/>
                                        </div>
                                    </span>
                                    <span className="lecture-info-title">가능한 시간대</span>
                                    <span>
                                        <span className={selectedTimeZoneData.filter(id => id === "morning").length !== 0? "day-btn day-active":"day-btn"} onClick = {() => {selectTimeZone("morning")}}>오전</span>
                                        <span className={selectedTimeZoneData.filter(id => id === "afternoon").length !== 0? "day-btn day-active":"day-btn"} onClick = {() => {selectTimeZone("afternoon")}}>오후</span>
                                        <span className={selectedTimeZoneData.filter(id => id === "evening").length !== 0? "day-btn day-active":"day-btn"} onClick = {() => {selectTimeZone("evening")}}>저녁</span>
                                    </span>
                                    <span className="lecture-info-title">가능한 요일</span>
                                    <span>
                                        <span className={selectedDayWeekData.filter(id => id === "월").length !== 0? "day-btn day-active":"day-btn"} onClick = {() => {selectDayWeek("월")}}>월</span>
                                        <span className={selectedDayWeekData.filter(id => id === "화").length !== 0? "day-btn day-active":"day-btn"} onClick = {() => {selectDayWeek("화")}}>화</span>
                                        <span className={selectedDayWeekData.filter(id => id === "수").length !== 0? "day-btn day-active":"day-btn"} onClick = {() => {selectDayWeek("수")}}>수</span>
                                        <span className={selectedDayWeekData.filter(id => id === "목").length !== 0? "day-btn day-active":"day-btn"} onClick = {() => {selectDayWeek("목")}}>목</span>
                                        <span className={selectedDayWeekData.filter(id => id === "금").length !== 0? "day-btn day-active":"day-btn"} onClick = {() => {selectDayWeek("금")}}>금</span>
                                        <span className={selectedDayWeekData.filter(id => id === "토").length !== 0? "day-btn day-active":"day-btn"} onClick = {() => {selectDayWeek("토")}}>토</span>
                                        <span className={selectedDayWeekData.filter(id => id === "일").length !== 0? "day-btn day-active":"day-btn"} onClick = {() => {selectDayWeek("일")}}>일</span>
                                    </span>
                                    <span></span>
                                    <WhiteButton name={calendar === true? "다시 반영하기":"캘린더에서 확인하기"} click={calendar === true? reCalendar:openCalendar}/>
                                </div>}

                                {calendar === true?
                                <CommonCalendar academyDates={academyDates} resultDates={resultDates} setdateData={setdateData}/>
                                :
                                null
                                }
                                <hr className="lecture-hr"/>
                                <div className="lecture-grid-layout2 mb15">
                                    <span className="lecture-info-title">강의 URL 주소</span>
                                    <input className="lecture-input mr15" onChange={(e) => { setAddLectureSampleUrl(e.target.value); }}/>
                                    <span className="lecture-info-title">강의 내용 설명</span>
                                    <textarea className="lecture-input mr15 h600"  onChange={(e) => { setAddLectureIntro(e.target.value); }}></textarea>
                                </div>
                                {/* <div className="button-wrapper">
                                    <GreyButton class="mr15" name="수정 완료"/>
                                    <GreyButton class="" name="강의 삭제"/>
                                </div> */}
                            </div>
                        </div>
                    </CommonModal>

                    <div className="mt50 table-row-lec">
                        <span className="th admin-subject">주제</span>
                        <span className="th">강의명</span>
                        <span className="th">강사명</span>
                        <span className="th admin-date">기간</span>
                        <span className="th admin-loc">강의 지역</span>
                    </div>
                    { allLecture !== null && currentPosts(allLecture).length !== 0 ?
                            currentPosts(allLecture).map((data, index) =>
                                <div key={index}  className = {selectedLecture !== null && selectedLecture.id === data.id ? "click-inst-row table-row-lec" : "table-row-lec"} onClick = {() => {clickLecture(data)}}>
                                    <span className="td admin-subject">{data.topic}</span>
                                    <span className="td">{data.name}</span>
                                    <span className="td">{data.instructor_name}</span>
                                    <span className="td admin-date">{data.date === "" || data.date.length === 0 ? "일정 미지정":data.date}</span>
                                    <span className="td admin-loc">{data.region}</span>
                                </div>
                            )
                            :
                            <div className="no-content">강의가 없습니다.</div>
                        }
                </div>
                <Pagination 
                    activePage={page} 
                    itemsCountPerPage={postsPerPage} 
                    totalItemsCount={allLecture === null ? 
                        0 :
                        isShowDelete ? 
                        allLecture.filter(data => data.del_date !== null).length:
                        allLecture.filter(data => data.del_date === null).length} 
                    pageRangeDisplayed={5} 
                    prevPageText={"‹"} 
                    nextPageText={"›"} 
                    onChange={handlePageChange} 
                />

                {selectedLecture !== null ?
                    editLecture ===true && editLectureInfo !== null ?
                    <div className="lecture-info-wrapper">
                        <p className="lecture-date">
                            <span>작성일 {editLectureInfo.reg_Date}</span>
                            <span className="lecture-update">수정일 {editLectureInfo.toLocaleString()}</span>
                        </p>
                        <div className="p4030">
                            <div className="lecture-grid-layout2 mb15">
                                <span className="lecture-info-title">강의 제목</span>
                                <input className="lecture-input" defaultValue={editLectureInfo.name} name="name" onChange={onEdit}/>
                            </div>
                            <div className="mb15">
                                <span className="lecture-info-title">강사명</span>
                                <span>{editLectureInfo.instructor_name} 강사</span>
                            </div>
                            <hr className="lecture-hr"/>
                            <div className="lecture-grid-layout mb15">
                                <span className="lecture-info-title">가능 지역</span>
                                <div className="category-wrapper">
                                    {regionDataByLecture !== null ?
                                    regionDataByLecture.map((data, index) => (
                                        <span 
                                            key={index} 
                                            className={data.status === 1 ? "category-btn category-active" : "category-btn"} 
                                            onClick={() => {data.id === 0 ? clickEveryRegion() : clickRegion(data)}}>
                                            {data.name}
                                        </span>
                                    ))
                                    :
                                    <div>선택 가능한 지역이 없습니다.</div>
                                    }
                                </div>
                            </div>
                            <div className="category-grid">
                                <span className="lecture-info-title">주제 카테고리</span>
                                <div className="category-wrapper">
                                    {topicDataByLecture !== null ?
                                    topicDataByLecture.map((data, index) => (
                                        <span key={index} className={data.status === 1 ? "category-btn category-active" : "category-btn"} onClick={() => clickTopic(data)}>{data.name}</span>
                                    ))
                                    :
                                    <div>선택 가능한 주제가 없습니다.</div>
                                    }
                                </div>
                            </div>
                            <hr className="lecture-hr"/>
                            {notUsingCalendar ?
                                <div className="mb40 lecture-grid-layout">
                                    <span className="lecture-info-title">가능한 날짜</span>
                                    <span>
                                        <div className="mt5">
                                            <label className="mr10">일정 미지정</label>
                                            <input type="checkbox" value={notUsingCalendar} onChange={(e) => {setresultDates(null); setnotUsingCalendar(!notUsingCalendar)}} checked/>
                                        </div>
                                    </span>
                                </div>
                                :
                                <div className="mb40 lecture-grid-layout">
                                    <span className="lecture-info-title">가능한 날짜</span>
                                    <span>
                                        <input className="lecture-input" name="start_date" defaultValue={editLectureInfo.start_date} onChange={onEdit} type="date"/> ~ <input className="lecture-input" name="end_date" onChange={onEdit} defaultValue={editLectureInfo.end_date} type="date"/>
                                        <div className="mt5">
                                            <label className="mr10">일정 미지정</label>
                                            <input type="checkbox" value={notUsingCalendar} onChange={(e) => {setnotUsingCalendar(!notUsingCalendar)}}/>
                                        </div>
                                    </span>
                                    <span className="lecture-info-title">가능한 시간대</span>
                                    <span>
                                        <span className={editLectureInfo.morning ? "day-btn day-active" : "day-btn"} onClick={()=>clickTimezone("morning")}>아침</span>
                                        <span className={editLectureInfo.afternoon ? "day-btn day-active" : "day-btn"} onClick={()=>clickTimezone("afternoon")}>점심</span>
                                        <span className={editLectureInfo.evening ? "day-btn day-active" : "day-btn"} onClick={()=>clickTimezone("evening")}>저녁</span>
                                    </span>
                                    <span className="lecture-info-title">가능한 요일</span>
                                    <span>
                                        <span className={editLectureInfo.day_week_array !== null && editLectureInfo.day_week_array !== undefined && editLectureInfo.day_week_array[1] === 1 ? "day-btn day-active": "day-btn"} onClick={()=>editDayClick(1)}>월</span>
                                        <span className={editLectureInfo.day_week_array !== null && editLectureInfo.day_week_array !== undefined && editLectureInfo.day_week_array[2] === 1 ? "day-btn day-active": "day-btn"} onClick={()=>editDayClick(2)}>화</span>
                                        <span className={editLectureInfo.day_week_array !== null && editLectureInfo.day_week_array !== undefined && editLectureInfo.day_week_array[3] === 1 ? "day-btn day-active": "day-btn"} onClick={()=>editDayClick(3)}>수</span>
                                        <span className={editLectureInfo.day_week_array !== null && editLectureInfo.day_week_array !== undefined && editLectureInfo.day_week_array[4] === 1 ? "day-btn day-active": "day-btn"} onClick={()=>editDayClick(4)}>목</span>
                                        <span className={editLectureInfo.day_week_array !== null && editLectureInfo.day_week_array !== undefined && editLectureInfo.day_week_array[5] === 1 ? "day-btn day-active": "day-btn"} onClick={()=>editDayClick(5)}>금</span>
                                        <span className={editLectureInfo.day_week_array !== null && editLectureInfo.day_week_array !== undefined && editLectureInfo.day_week_array[6] === 1 ? "day-btn day-active": "day-btn"} onClick={()=>editDayClick(6)}>토</span>
                                        <span className={editLectureInfo.day_week_array !== null && editLectureInfo.day_week_array !== undefined && editLectureInfo.day_week_array[0] === 1 ? "day-btn day-active": "day-btn"} onClick={()=>editDayClick(0)}>일</span>
                                    </span>
                                    <WhiteButton name="다시 반영하기" click={reEditCalendar}/>
                                </div>
                                }
                                {!notUsingCalendar && editcalendar ?
                                <LectureCalendar 
                                    academyDates={academyDates}
                                    calendarData={resultDates} 
                                    path={window.location.href.replace(process.env.REACT_APP_DEFAULT_WHOLE_URL, "/")}
                                    createDate={createDate}
                                    // updateDate={updateDate}
                                    deleteDate={deleteDate}
                                    lectureId={editLectureInfo.id}
                                    setResultDates={setresultDates}
                                />:
                                <div></div>
                            }
                            <hr className="lecture-hr"/>
                            <div className="lecture-grid-layout2 mb15">
                                <span className="lecture-info-title">강의 URL 주소</span>
                                <input className="lecture-input mr15" defaultValue={editLectureInfo.sample_url} name="sample_url" onChange={onEdit}/>
                                <span className="lecture-info-title">강의 내용 설명</span>
                                <textarea className="lecture-input mr15 h600" defaultValue={selectedLecture.intro.replaceAll("<br/>","\n")} name="intro" onChange={onEdit}/>
                            </div>
                            <div className="button-wrapper">
                                <GreyButton class="mr15" name="수정 완료" click={updateFinishBtn}/>
                                <GreyButton class="" name="수정 취소" click={cancelBtnClick}/>
                            </div>
                        </div>
                    </div>
                    :
                    <div className="show-lecture-detail">
                        <div className="detail-title">
                            <h2>{selectedLecture.name}</h2>
                            <span>{selectedLecture.instructor_name} 작성</span>
                            <span className="lecture-detail-date">강의가능기간: {selectedLecture.date !== null && selectedLecture.date !== ""? selectedLecture.date : "일정 미지정"}</span>
                        </div>
                        <div className="detail-content">
                            <div className="detail-sub-info">
                                <span>강의 가능 지역</span>
                                <span>: {selectedLecture.region} </span>
                                <span>강의 주제 카테고리</span>
                                <span>: {selectedLecture.topic}</span>
                                {selectedLecture.sample_url !== null && selectedLecture.sample_url !== "" && selectedLecture.sample_url !== "null"? <span>강의 URL 주소</span>:null}
                                {selectedLecture.sample_url !== null && selectedLecture.sample_url !== "" && selectedLecture.sample_url !== "null"? <span>: <a href={selectedLecture.sample_url}>{selectedLecture.sample_url}</a></span>:null}
                            </div>
                            <p className="mb30">
                                {selectedLecture.intro.split("<br/>").map( (item, i) => <div key={i}>{item}</div>)}
                            </p>
                            {
                                selectedLecture.date.length === 0?
                                "일정 추후 문의":
                                <div className="lecture-inst-calendar">
                                    <ReadCalendar
                                        academyDates={academyDates}
                                        readMainCalendar={readMainCalendar} 
                                        calendarData={calendarInfo} 
                                        path={window.location.href.replace(process.env.REACT_APP_DEFAULT_WHOLE_URL, "/")} 
                                        clickedLecture={selectedLecture}/>
                                </div>
                            }
                            {/* <div className="lecture-inst-calendar">
                                <ReadCalendar academyDates={academyDates} readMainCalendar={readMainCalendar} calendarData={calendarInfo} path={window.location.href.replace(process.env.REACT_APP_DEFAULT_WHOLE_URL, "/")} clickedLecture={selectedLecture}/>
                            </div> */}
                            {isShowDelete ?
                            <div className="button-wrapper">
                                <GreyButton class="" name="복구하기" click={deleteToExist}/>
                            </div>
                            :
                            <div className="button-wrapper">
                                <GreyButton class="mr15" name="수정하기" click={editBtnClick}/>
                                <GreyButton class="" name="삭제하기" click={deleteBtnClick}/>
                            </div>
                            }
                        </div>

                    </div>
                    :
                    null
                    }
            </div>
        </div>
    )

}

export default AdminLecture;
